function onEntry(e) {
  e.forEach((e) => {
    e.isIntersecting && e.target.classList.add("element-show");
  });
}
let options = { threshold: [0.5] },
  observer = new IntersectionObserver(onEntry, options),
  elements = document.querySelectorAll(".element-animation");
for (let e of elements) observer.observe(e);
$(".burger__mob").on("click", function (e) {
  $(this).toggleClass("active"),
    $(".header").toggleClass("active"),
    $("body").toggleClass("hidden");
})
$(".burger__middle").on("click", function (e) {
    $(".header__middle").toggleClass("active")
  })
  $.fn.setCursorPosition = function (e) {
    if ($(this).get(0).setSelectionRange)
      $(this).get(0).setSelectionRange(e, e);
    else if ($(this).get(0).createTextRange) {
      var t = $(this).get(0).createTextRange();
      t.collapse(!0),
        t.moveEnd("character", e),
        t.moveStart("character", e),
        t.select();
    }
  }
  $("input[type=tel]")
    .click(function () {
      $(this).setCursorPosition(3);
    })
    .mask("+7(999) 999-9999"),
  SmoothScroll({
    animationTime: 800,
    stepSize: 45,
    accelerationDelta: 30,
    accelerationMax: 1,
    keyboardSupport: !0,
    arrowScroll: 100,
    pulseAlgorithm: !0,
    pulseScale: 4,
    pulseNormalize: 1,
    touchpadSupport: !0,
  });
